good_pattern.listener_code:
// ‚úÖ GOOD: Listen for auth state changes
if (code) {
  console.log('üîê Magic link detected, setting up auth listener...');

  // Listen for auth state changes
  const { data: { subscription } } = supabase.auth.onAuthStateChange((event, session) => {
    console.log('üîê Auth state change:', event, session?.user?.email);

    if (event === 'SIGNED_IN' && session?.user) {
      console.log('‚úÖ User signed in:', session.user.email);
      router.push('/dashboard');
      subscription.unsubscribe();
    }
  });

  // Also check for existing session
  const { data: sessionData } = await supabase.auth.getSession();
  if (sessionData.session && sessionData.session.user) {
    console.log('‚úÖ Session already exists:', sessionData.session.user.email);
    router.push('/dashboard');
    subscription.unsubscribe();
  }
}

