good_pattern.edge_function_code:
import { Resend } from 'https://esm.sh/resend@2.0.0';

Deno.serve(async (req: Request) => {
  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response('ok', {
      headers: {
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
        'Access-Control-Allow-Methods': 'POST, OPTIONS',
      },
    });
  }

  try {
    const { user_id, email, full_name } = await req.json();

    // Initialize Resend with environment variable
    const resend = new Resend(Deno.env.get('RESEND_API_KEY'));

    // Send actual email
    const { data, error } = await resend.emails.send({
      from: 'Supabase Stripe App <onboarding@resend.dev>',
      to: [email],
      subject: 'Welcome to Supabase Stripe App! üéâ',
      html: `<!-- Professional HTML email template -->`,
      text: `<!-- Plain text fallback -->`
    });

    if (error) throw error;

    return new Response(JSON.stringify({ 
      success: true, 
      resend_id: data?.id 
    }), {
      headers: { 
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*' 
      }
    });
  } catch (error) {
    return new Response(JSON.stringify({ error: error.message }), {
      status: 500,
      headers: { 
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*' 
      }
    });
  }
});

