good_pattern.contact_page_code:
'use client';
import { useState } from 'react';

export default function ContactPage() {
  const [formData, setFormData] = useState({
    name: '', email: '', subject: '', message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState('idle');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setSubmitStatus('success');
        setFormData({ name: '', email: '', subject: '', message: '' });
      } else {
        setSubmitStatus('error');
      }
    } catch (error) {
      setSubmitStatus('error');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={formData.name} onChange={handleInputChange} required />
      <input name="email" type="email" value={formData.email} onChange={handleInputChange} required />
      <input name="subject" value={formData.subject} onChange={handleInputChange} required />
      <textarea name="message" value={formData.message} onChange={handleInputChange} required />
      <button type="submit" disabled={isSubmitting}>Send Message</button>
    </form>
  );
}

good_pattern.database_schema_sql:
CREATE TABLE IF NOT EXISTS contact_messages (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  name TEXT NOT NULL,
  email TEXT NOT NULL,
  subject TEXT NOT NULL,
  message TEXT NOT NULL,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  status TEXT DEFAULT 'new' CHECK (status IN ('new', 'in_progress', 'resolved', 'closed')),
  auto_reply_sent BOOLEAN DEFAULT FALSE,
  auto_reply_sent_at TIMESTAMP WITH TIME ZONE
);

ALTER TABLE contact_messages ENABLE ROW LEVEL SECURITY;
CREATE POLICY "Allow public to insert contact messages" ON contact_messages FOR INSERT WITH CHECK (true);
CREATE POLICY "Allow authenticated users to read contact messages" ON contact_messages FOR SELECT USING (auth.role() = 'authenticated');

good_pattern.api_route_code:
import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@/lib/supabase/server';

export async function POST(request: NextRequest) {
  try {
    const { name, email, subject, message } = await request.json();

    // Validate required fields
    if (!name || !email || !subject || !message) {
      return NextResponse.json({ error: 'Missing required fields' }, { status: 400 });
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      return NextResponse.json({ error: 'Invalid email format' }, { status: 400 });
    }

    const supabase = await createClient();

    // Insert contact message
    const { data, error } = await supabase
      .from('contact_messages')
      .insert([{ name, email, subject, message }])
      .select()
      .single();

    if (error) {
      return NextResponse.json({ error: 'Failed to save contact message' }, { status: 500 });
    }

    // Call edge function for auto-reply
    try {
      const edgeFunctionUrl = `${process.env.NEXT_PUBLIC_SUPABASE_URL}/functions/v1/contact-auto-reply`;
      await fetch(edgeFunctionUrl, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${process.env.SUPABASE_SERVICE_ROLE_KEY}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          type: 'INSERT',
          table: 'contact_messages',
          record: data,
          schema: 'public',
          old_record: null
        })
      });
    } catch (error) {
      console.error('Error calling edge function:', error);
    }

    return NextResponse.json({ message: 'Contact message saved successfully', id: data.id }, { status: 201 });
  } catch (error) {
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

good_pattern.edge_function_code:
import 'jsr:@supabase/functions-js/edge-runtime.d.ts';
import { createClient } from 'jsr:@supabase/supabase-js@2';

Deno.serve(async (req: Request) => {
  try {
    const payload = await req.json();
    const contactMessage = payload.record;

    if (contactMessage.auto_reply_sent) {
      return new Response(JSON.stringify({ message: 'Auto-reply already sent' }), { status: 200 });
    }

    const supabase = createClient(
      Deno.env.get('SUPABASE_URL')!,
      Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!
    );

    // Create auto-reply email content
    const autoReplySubject = `Re: ${contactMessage.subject} - We've received your message`;
    const autoReplyMessage = `Dear ${contactMessage.name},\n\nThank you for contacting us! We have received your message regarding "${contactMessage.subject}" and will get back to you within 24 hours.\n\nYour message:\n"${contactMessage.message}"\n\nWe appreciate your interest and look forward to helping you.\n\nBest regards,\nThe Support Team`;

    // Send email via Resend
    const emailResponse = await fetch('https://api.resend.com/emails', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${Deno.env.get('RESEND_API_KEY')}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        from: 'onboarding@resend.dev',
        to: [contactMessage.email],
        subject: autoReplySubject,
        text: autoReplyMessage,
        html: `<div>${autoReplyMessage.replace(/\n/g, '<br>')}</div>`
      })
    });

    if (emailResponse.ok) {
      // Update contact message to mark auto-reply as sent
      await supabase
        .from('contact_messages')
        .update({ auto_reply_sent: true, auto_reply_sent_at: new Date().toISOString() })
        .eq('id', contactMessage.id);
    }

    return new Response(JSON.stringify({ message: 'Auto-reply processed successfully' }), { status: 200 });
  } catch (error) {
    return new Response(JSON.stringify({ error: 'Internal server error' }), { status: 500 });
  }
});
